
-- criando a maskter
CREATE COLUMN MASTER KEY [Cmk_Cript_I4pro]
WITH
(
    KEY_STORE_PROVIDER_NAME = N'MSSQL_CERTIFICATE_STORE',
    KEY_PATH = N'CurrentUser/my/D8CDCC7BC22069DEC1FCF32A5C349FCD33424FAA'
)
GO
 
 -- criando a column encrypt keys
CREATE COLUMN ENCRYPTION KEY [CEK_cript_I4pro]
WITH VALUES
(
    COLUMN_MASTER_KEY = [Cmk_Cript_I4pro],
    ALGORITHM = 'RSA_OAEP',
    ENCRYPTED_VALUE = 
)
GO
 
 create TABLE [dbo].[corp_pessoas_criptografia](
	[Id_pessoa] [int] IDENTITY(1,1) NOT NULL,
	[Cd_tp_registro] [int] NOT NULL,
	[Cd_tipo_pessoa] [int] NOT NULL,
	[Cd_profissao] [int] NULL,
	[Id_pessoa_pai] [int] NULL,
	[Cd_filial] [int] NULL,
	[Nr_cnpj_cpf] [bigint] NULL,
	[Nm_pessoa] [VARCHAR](190) )
	
	-- alterando a coluna e atribuindo a column encrypt key
	alter table [dbo].[corp_pessoas_criptografia]
	alter column  nm_pessoa varchar(190)
	collate latin1_general_bin2  ENCRYPTED WITH (COLUMN_ENCRYPTION_KEY = [CEK_cript_I4pro], ENCRYPTION_TYPE = RANDOMIZED, ALGORITHM = 'AEAD_AES_256_CBC_HMAC_SHA_256')

	
	alter table [dbo].[corp_pessoas_criptografia]
	add nm_endereco varchar(190) 
	collate latin1_general_bin2  ENCRYPTED WITH (COLUMN_ENCRYPTION_KEY = [CEK_cript_I4pro], ENCRYPTION_TYPE = RANDOMIZED, ALGORITHM = 'AEAD_AES_256_CBC_HMAC_SHA_256')


SELECT
    DB_NAME() AS [database],
    t.[name] AS [table],
    c.[name] AS [column],
    c.encryption_algorithm_name,
    c.[encryption_type],
    c.encryption_type_desc,
	cek.name
FROM
    sys.columns c
    JOIN sys.tables t 
	ON t.[object_id] = c.[object_id]
	left join sys.column_encryption_keys cek
	on c.column_encryption_key_id = cek.column_encryption_key_id
WHERE
    c.encryption_algorithm_name IS NOT NULL


	select * from sys.column_encryption_keys

	
	select name,is_master_key_encrypted_by_server from sys.databases
	

create TABLE [dbo].[corp_pessoas_criptografia](
	[Id_pessoa] [int] IDENTITY(1,1) NOT NULL,
	[Cd_tp_registro] [int] NOT NULL,
	[Cd_tipo_pessoa] [int] NOT NULL,
	[Cd_profissao] [int] NULL,
	[Id_pessoa_pai] [int] NULL,
	[Cd_filial] [int] NULL,
	[Nr_cnpj_cpf] [bigint] NULL,
	[Nm_pessoa] [VARCHAR](180) collate latin1_general_bin2  ENCRYPTED WITH (COLUMN_ENCRYPTION_KEY = [CEK_cript_I4pro], ENCRYPTION_TYPE = RANDOMIZED, ALGORITHM = 'AEAD_AES_256_CBC_HMAC_SHA_256') NULL)
	